// SPDX-License-Identifier: MIT
pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface UniswapV2Router {
    function WETH() external pure returns (address);

    function getAmountsOut(uint amountIn, address[] memory path) external view returns (uint[] memory amounts);

    function getAmountsIn(uint amountOut, address[] memory path) external view returns (uint[] memory amounts);

    // ETH to DAI
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
    external
    payable
    returns (uint[] memory amounts);

    // DAI to ETH
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
    external
    returns (uint[] memory amounts);
}

interface AaveETHRouter {
    function depositETH(
        address lendingPool,
        address onBehalfOf,
        uint16 referralCode
    ) external payable;

    function withdrawETH(
        address lendingPool,
        uint256 amount,
        address onBehalfOf
    ) external;

    function repayETH(
        address lendingPool,
        uint256 amount,
        uint256 rateMode,
        address onBehalfOf
    ) external payable;

    function borrowETH(
        address lendingPool,
        uint256 amount,
        uint256 interesRateMode,
        uint16 referralCode
    ) external;
}

interface AavePoolRouter {
    function deposit(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external;

    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256);

    function borrow(
        address asset,
        uint256 amount,
        uint256 interestRateMode,
        uint16 referralCode,
        address onBehalfOf
    ) external;

    function repay(
        address asset,
        uint256 amount,
        uint256 rateMode,
        address onBehalfOf
    ) external returns (uint256);
}

contract SubWallet {
    // Events
    event Deposit(address, address, uint256);
    event Withdraw(address, uint256);
    event ShortStart();
    event ShortEnd();
    event ChangeOwner();
    event Receive(address, uint256);

    // Convertor
    using SafeERC20 for IERC20;

    // Clone
    // -------------------------------------------------------------------------
    bool private _isBase;

    address private _owner;
    modifier onlyOwner() {
        require(msg.sender == _owner, "ERROR: Only Owner");
        _;
    }

    constructor() { // base contract
        _isBase = true;
    }

    // only use at first
    function initialize(address _initOwner, address _uni, address _ethAllow, address _aaveEth, address _aavePool, address[] memory _deposit, address[] memory _short) external {
        require(_isBase == false, "This is the base contract");
        require(_owner == address(0), "Already initialized");
        _owner = _initOwner;

        uniSwapRouter = UniswapV2Router(_uni);
        aaveEthAllow = _ethAllow;
        aaveGateWay = AaveETHRouter(_aaveEth);
        aaveLendingPool = AavePoolRouter(_aavePool);

        depositTokenWhitelist = _deposit;
        shortTokenWhitelist = _short;
        shortTokenWhitelist.push(address(this));
    }

    // Wallet Field
    // -------------------------------------------------------------------------
    // External
    UniswapV2Router uniSwapRouter;
    AavePoolRouter aaveLendingPool;
    AaveETHRouter aaveGateWay;
    address aaveEthAllow;
    address[] depositTokenWhitelist;
    address[] shortTokenWhitelist;
    uint commissionFee = 50; // 0.5 Percentage

    // User Info
    mapping(address => uint256) collateralList; // DepositToken => Collateral Amount
    mapping(address => Short) shortList; // ShortToken => Short Amount
    Commission[] commissions; // CollateralToken => Commission

    struct Short {
        address token; // 담보 토큰
        uint256 amount; // 빌린 금액 ETH
        uint256 price; // 바뀐 금액 Token / 담보 토큰과 같은 토큰
    }

    struct Commission {
        address token;
        uint256 fee;
    }

    // Modifier
    modifier checkDeposit(address _token) {
        for (uint i = 0; i < depositTokenWhitelist.length; i++) {
            require(depositTokenWhitelist[i] == _token, "Not Valid Token");
        }
        _;
    }

    modifier checkShort(address _token) {
        for (uint i = 0; i < shortTokenWhitelist.length; i++) {
            require(shortTokenWhitelist[i] == _token, "Not Valid Token");
        }
        _;
    }

    // Token action
    // -------------------------------------------------------------------------
    function approveEth(address _to, uint256 _amount) external {
        (bool success,) = address(aaveEthAllow).call{gas : 200000}(
            abi.encodeWithSignature("approveDelegation(address,uint256)", _to, _amount)
        );
        require(success, "FAILED");
    }

    function approveTo(address _token, address _to, uint _amount) external onlyOwner {
        (bool success,) = address(_token).call{gas : 200000}(
            abi.encodeWithSignature("approve(address,uint256)", _to, _amount)
        );
        require(success, "FAILED");
    }

    function depositToken(address _token, uint256 _amount) external checkDeposit(_token) {
        IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        emit Deposit(_token, msg.sender, _amount);
    }

    function withdrawToken(address _token, uint256 _amount) external {
        require(IERC20(_token).balanceOf(address(this)) >= _amount, "Not enough token");
        IERC20(_token).transfer(msg.sender, _amount);
        emit Withdraw(_token, _amount);
    }

    // Product action
    // -------------------------------------------------------------------------
    function shortStartETH(address _collateralToken, uint256 _collateralAmount, uint256 _ethAmount) external {
        bool sResult = supply(_collateralToken, _collateralAmount);
        require(sResult, "Supply Failed");
        collateralList[_collateralToken] = _collateralAmount;

        bool bResult = borrowETH(_ethAmount);
        require(bResult, "Borrow Failed");

        uint changeAmount = ethToToken(_collateralToken, _ethAmount)[1];
        shortList[address(this)] = Short(_collateralToken, _ethAmount, changeAmount);
    }

    // token to token
    function shortStart(address _collateralToken, uint256 _collateralAmount, address _shortToken, uint256 _shortAmount) external onlyOwner checkShort(_shortToken) {
    }

    // ETH => DAI
    function ethToToken(address _token, uint256 _ethAmount) public returns (uint[] memory) {
        uint tokenAmount = _getAmountToken(_token, _ethAmount)[1];
        address[] memory path = new address[](2);
        path[0] = uniSwapRouter.WETH();
        path[1] = _token;
        uint deadline = block.timestamp + 100;

        uint[] memory amounts = uniSwapRouter.swapExactETHForTokens{value : uint(_ethAmount)}(tokenAmount, path, payable(address(this)), deadline);

        return amounts;
    }

    function supplyETH() internal returns (bool) {
        return false;
    }

    function supply(address _token, uint256 _amount) internal returns (bool) {
        try aaveLendingPool.deposit(_token, _amount, address(this), 0) {
            return true;
        }
        catch {
            return false;
        }
    }

    function borrowETH(uint256 _ethAmount) internal returns (bool) {
        try aaveGateWay.borrowETH(address(aaveLendingPool), _ethAmount, 2, 0) {
            return true;
        }
        catch {
            return false;
        }
    }

    function borrow(address _token, uint256 _amount) internal returns (bool) {
        return false;
    }

    function _getAmountToken(address _token, uint256 _ethAmount) internal view returns (uint[] memory) {
        address[] memory path = new address[](2);
        path[0] = uniSwapRouter.WETH();
        path[1] = _token;

        return uniSwapRouter.getAmountsOut(_ethAmount, path);
    }

    // -------------------------------------------------------------------------
    function shortEndETH() external {
        Short memory shorted = shortList[address(this)];
        tokenToEth(shorted.token, shorted.amount);

        bool rResult = _repayETH(shorted.amount);
        require(rResult, "Repay Fail");

        bool wResult = _withdraw(shorted.token, collateralList[shorted.token]);
        require(wResult, "Withdraw Fail");

        collateralList[shorted.token] = 0;
        shortList[address(this)] = Short(address(0), 0, 0);
    }

    function _repayETH(uint256 _ethAmount) internal returns (bool) {
        try aaveGateWay.repayETH{value : _ethAmount}(address(aaveLendingPool), _ethAmount, 2, address(this)) {
            return true;
        }
        catch {
            return false;
        }
    }

    function _repay(uint256 amount) internal returns (bool) {
        return false;
    }

    function _withdraw(address _token, uint256 _amount) internal returns (bool) {
        try aaveLendingPool.withdraw(_token, _amount, address(this)) {
            return true;
        }
        catch {
            return false;
        }
    }

    // DAI to Token / ETH
    // DAI => ETH
    function tokenToEth(address _token, uint256 _ethAmount) public returns (uint[] memory) {
        uint tokenAmount = _getAmountETH(_token, _ethAmount)[0];

        address[] memory path = new address[](2);
        path[0] = _token;
        path[1] = uniSwapRouter.WETH();
        uint deadline = block.timestamp + 100;

        uint[] memory amounts = uniSwapRouter.swapExactTokensForETH(tokenAmount, _ethAmount, path, address(this), deadline);
        require(amounts[0] != 0, "TOKEN => ETH FAIL");

        return amounts;
    }

    function _getAmountETH(address _token, uint256 _ethAmount) internal view returns (uint[] memory) {
        address[] memory path = new address[](2);
        path[0] = _token;
        path[1] = uniSwapRouter.WETH();

        return uniSwapRouter.getAmountsIn(_ethAmount, path);
    }

    // View
    // -------------------------------------------------------------------------
    function getShort(address _token) external view returns (address, uint, uint) {
        Short memory shorted = shortList[_token];
        return (shorted.token, shorted.amount, shorted.price);
    }

    function uniSwapAdd() external view returns (address) {
        return address(uniSwapRouter);
    }

    function aavePoolAdd() external view returns (address) {
        return address(aaveLendingPool);
    }

    function aaveEthAllowAdd() external view returns (address) {
        return address(aaveEthAllow);
    }

    function aaveGateAdd() external view returns (address) {
        return address(aaveGateWay);
    }

    function getDepositList() external view returns (address[] memory) {
        return depositTokenWhitelist;
    }

    function getShortList() external view returns (address[] memory) {
        return shortTokenWhitelist;
    }

    // Owner Functions
    // -------------------------------------------------------------------------
    function setCommissionFee(uint256 fee) external onlyOwner { // only factory
        commissionFee = fee;
    }

    function setUniSwapAdd(address newAdd) external onlyOwner {
        uniSwapRouter = UniswapV2Router(newAdd);
    }

    function setAaveEthAllowAdd(address newAdd) external onlyOwner {
        aaveEthAllow = newAdd;
    }

    function setAaveLendingAdd(address newAdd) external onlyOwner {
        aaveLendingPool = AavePoolRouter(newAdd);
    }

    function setAaveGateWayAdd(address newAdd) external onlyOwner {
        aaveGateWay = AaveETHRouter(newAdd);
    }

    function addDepositTokenAdd(address newAdd) external onlyOwner {
        depositTokenWhitelist.push(newAdd);
    }

    function addShortTokenAdd(address newAdd) external onlyOwner {
        shortTokenWhitelist.push(newAdd);
    }

    receive() external payable {
        emit Receive(msg.sender, msg.value);
    }
}
